import graphic1 from "./imgs/brand-article.png";
import graphic2 from "./imgs/HMrating.png";
import kotn from "./imgs/kotn.png";
import pact from "./imgs/pact.png";
import hos from "./imgs/house-of-sunny.png";
import React, {Component} from 'react';

class WriteUpPage extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      brand: {}
    };
  }

  componentDidMount() {
    fetch("https://api.root.quest/brandsearch?name=Nike")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            brand: result
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      let envRating = this.state.brand.Environment;
      let ethRating = this.state.brand.EthicalPractices;
      let transparency = this.state.brand.Transparency;
      let brandName = this.state.brand.BrandName;
      return (
        <div id = "writeup">
          <div class = "row-container" style = {{marginBottom: '20px'}}>
            <img src = {graphic1}></img>
            <div id = 'writeUpLeft'>
              <h1>{brandName}</h1>
              <p>A summary of the brand will go here. Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam</p>
            </div>
          </div>

          <div id = "writeup-category">
              <h2>Our rating</h2>
              <div class = "row-container" style = {{marginBottom: '20px'}}>
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam</p>
              <img id = "writeup-rating-scale" src = {graphic2}></img>
            </div>

          </div>

          <div id = "writeup-category">
            <h2>Sustainable materials</h2>
            <p>{brandName}’s sustainable materials rating is {envRating}. {brandName} uses [little to no/ some / many]  sustainable materials [such as [tag]]. [Brand] has [good/bad] policies for animal hair use such as [tag]. [Brand] [does/does not] practice waste management through [tag]. Click here for more explanation on [tag]</p>
          </div>

          <div id = "writeup-category">
            <h2>Ethical Practices</h2>
            <p>{brandName}’s ethical practices rating is {ethRating}. {brandName} has [little to no/ some/ many] initiatives to protect their workers [such as [tag]]. Click here for more information on [tag].</p>
          </div>


          <div id = "writeup-category">
            <h2>Brand transparency</h2>
            <p>{brandName}’s Supply chain transparency rating is {transparency}. {brandName} has [little to no] supply chain transparency. {brandName} [has/ does not have] reports available online about their practices. The brand also has certifications such as [tag] which is evidence for transparency in the supply chain.</p>
          </div>

          <div id = "writeup-category">
            <h2>Sustainable Alternatives</h2>
            <div class ="row-container">
              <img id = "alternativeImg" src = {kotn}></img>
              <img id = "alternativeImg" src = {pact}></img>
              <img id = "alternativeImg" src = {hos}></img>
            </div>
          </div>
        </div>



  );
  }
}
}

export default WriteUpPage;
