import graphic1 from "./imgs/brand-article.png";
import graphic2 from "./imgs/HMrating.png";
import kotn from "./imgs/kotn.png";
import pact from "./imgs/pact.png";
import hos from "./imgs/house-of-sunny.png";
import React, {Component} from 'react';

class WriteUpPage extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      brand: {}
    };
  }

  componentDidMount() {
    fetch("https://api.root.quest/brandsearch?name=Nike")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            brand: result
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      let SusTags = [
        "100% organic cotton",
        "Recycled materials",
        "No exotic animal use",
        "Eco-friendly materials",
