import './App.css';
import TopNav from './TopNav.js';
import graphic from './imgs/Open_Doodles.png';
import graphic2 from './imgs/rating-scale.png';
import graphic3 from './imgs/browser-screencaps.png';
import graphic4 from './imgs/search-bar.png';
import graphic5 from './imgs/fact-image1.png';
import graphic6 from './imgs/fact-image2.png';

function App() {
  return (
    <div style = {{backgroundColor: "#F5EFE2", height: "100vh", width: "100wh"}}>
      <TopNav></TopNav>

      <div style ={{display: "flex", flex: 1, alignContent:"center", justifyContent: "center", marginTop: 30, marginBottom: 30}}> 
        <p style = {{fontSize: 50}}>We do the research so you donâ€™t have to.</p>
      </div>


      <div class = "row-container">
        <img src = {graphic2}/>
        <p>When visiting a clothing<br></br> retail/marketplace website, an<br></br>indicator will appear rating that <br></br> brand in terms of sustainability and<br></br> ethical practices. </p>
      </div>

      <div class = "row-container">
        <p>Click our icon on the chrome <br></br>toolbar to learn information about <br></br>the companyâ€™s practices.</p>
        <img src = {graphic3}/>
      </div>

      <div class = "row-container">
        <img src = {graphic4}/>
        <p>Search for brands/online <br></br>marketplaces through our <br></br>brand index.</p>
      </div>

      <div class = "row-container">
        <img src = {graphic5}/>
        <p>The fashion industry is the second largest polluter in the world.</p>
        <p>When we only treat garments as disposable, we contribute to negative consumption habits that ultimately lead to detrimental effects to the environment.</p>
      </div>

      <div>
        </div>

      <div class = "row-container">
        <img src = {graphic6}/>
        <p>We demand radical transparency.</p>
      </div>

    </div>

  );
}

export default App;
